import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Router } from '@angular/router';
import { Ucenik } from '../models/ucenik';
import { Nastavnik } from '../models/nastavnik';
import { Cas } from '../models/cas';


@Component({
  selector: 'app-ucenik',
  templateUrl: './ucenik.component.html',
  styleUrls: ['./ucenik.component.css']
})
export class UcenikComponent {
  constructor(private servis : LoginservisService, private ruter : Router ){}
  loggedUcenik : Ucenik = new Ucenik()
  editedUcenik : Ucenik = new Ucenik()
  where : number = 0
  called : number = 0
  nastavnici : Nastavnik[] = []
  search : string = ""
  odrzani : Cas[] = []
  neodrzani : Cas[] = []
  searchIme: string = '';
  searchPrezime: string = '';
  imgURL : string = "http://localhost:4000/uploads/1707042431780.jpg"
  imgRel: string = "http://localhost:4000"
  selectedFile: File | null = null;
  imageUrl : string = ""

  ngOnInit(){
    this.imageUrl = ""
    this.selectedFile = null
    this.where = 1
    this.called = 0
    let logged = localStorage.getItem("logged")
    if(logged){
      this.servis.getucenik(logged).subscribe(user=>{
        this.loggedUcenik = user;
        this.editedUcenik = new Ucenik();
        this.imgRel += user.profilePic


      })
      if(this.loggedUcenik.schooltype ==="osnovna" && this.loggedUcenik.schoolgrade < 5 ){
        this.servis.getnastavniciporazred(1).subscribe( nst =>{
          this.nastavnici = nst
        })
      }else if(this.loggedUcenik.schooltype ==="osnovna"){
        this.servis.getnastavniciporazred(2).subscribe( nst =>{
          this.nastavnici = nst
        })
      }else{

        this.servis.getnastavniciporazred(3).subscribe( nst =>{

          this.nastavnici = nst
        })
      }
      this.servis.casget(1,logged).subscribe(data=>{
        if(data){
          this.odrzani = data
        }
      })
      this.servis.casget(0,logged).subscribe(data=>{
        if(data){
          this.neodrzani = data
        }
      })

    }
  }


  changeWhere(value: number): void {
    this.where = value;
  }

  saveChanges(){

    this.editedUcenik.username = this.loggedUcenik.username
    if(this.editedUcenik.name  === "")this.editedUcenik.name = this.loggedUcenik.name
    if(this.editedUcenik.surname  === "")this.editedUcenik.surname = this.loggedUcenik.surname
    if(this.editedUcenik.adress  === "")this.editedUcenik.adress = this.loggedUcenik.adress
    if(this.editedUcenik.email  === "")this.editedUcenik.email = this.loggedUcenik.email
    if(this.editedUcenik.telephone  === 0)this.editedUcenik.telephone = this.loggedUcenik.telephone
    if(this.editedUcenik.schooltype  === "")this.editedUcenik.schooltype = this.loggedUcenik.schooltype
    if(this.loggedUcenik.schooltype  === "osnovna" && this.editedUcenik.schooltype !== "osnovna" && this.editedUcenik.schooltype !== "" ){

      this.editedUcenik.schoolgrade = 1
    }
    if(this.selectedFile !== null){
      this.uploadTest().then(() =>{
        this.editedUcenik.profilePic = this.imageUrl
        alert(this.editedUcenik.profilePic)
        this.servis.updateucenik(this.editedUcenik).subscribe(data=>{

        })
        this.ngOnInit()
      })

    }else{
      this.servis.updateucenik(this.editedUcenik).subscribe(data=>{

      })
      this.ngOnInit()
    }





  }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  incrementSchoolGrade(){
    this.called = 1
    let num = this.loggedUcenik.schoolgrade

    if(this.loggedUcenik.schooltype  === "osnovna" && num == 8){
      alert("Ne moze se povecati razred dok se ne prebacite u srednju!!")
      this.editedUcenik.schoolgrade =  this.loggedUcenik.schoolgrade
      return;
    }
    if(num == 4 && this.loggedUcenik.schooltype !== "osnovna"){
      alert("Cetvrta godina srednje ne mozete povecavati godinu")
      this.editedUcenik.schoolgrade =  this.loggedUcenik.schoolgrade
      return
    }
    num++;
    this.editedUcenik.schoolgrade = num;


  }


  filterNastavnici(){
    this.nastavnici.forEach(nastavnik => {
      const imeMatch = this.searchIme ? nastavnik.name.toLowerCase().includes(this.searchIme.toLowerCase()) : true;
      const prezimeMatch = this.searchPrezime ? nastavnik.surname.toLowerCase().includes(this.searchPrezime.toLowerCase()) : true;


      nastavnik.visible = !(imeMatch && prezimeMatch) ;
    });
  }



  sortByPrezime(ascending: boolean){
    this.nastavnici.sort((a, b) => {
      const nameA = a.surname.toLowerCase();
      const nameB = b.surname.toLowerCase();
      if (ascending) {
        return nameA.localeCompare(nameB);
      } else {
        return nameB.localeCompare(nameA);
      }
    });
  }


  sortByIme(ascending: boolean){
    this.nastavnici.sort((a, b) => {
      const nameA = a.name.toLowerCase();
      const nameB = b.name.toLowerCase();
      if (ascending) {
        return nameA.localeCompare(nameB);
      } else {
        return nameB.localeCompare(nameA);
      }
    });
  }

  LogOut(){
    localStorage.clear
    this.ruter.navigate([''])
  }


  uploadTest(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      this.servis.uploadPicture(this.selectedFile!).subscribe(data => {
        if (data && data.filename) {
          console.log('File uploaded successfully.');
          this.imageUrl = data.filename;
          console.log(this.imageUrl);
          resolve();
        } else {
          reject(new Error('Upload failed'));
        }
      });
    });
  }



}

<header class="navbar navbar-dark-grey">
  <div class="container">
    <div class="navbar-right">
      <span (click)="changeWhere(1)">Profil  </span>
      <span (click)="changeWhere(2)">Nastavnici  </span>
      <span (click)="changeWhere(3)">Časovi  </span>
      <span (click)="LogOut()">Log out</span>
    </div>
  </div>

</header>
<body>





  <main>
    <div *ngIf="where === 1">
      <div class="row">
        <div class="col-md-12">
          <div class = "card" style = "width:400px">
            <img class = "card-img-top" [src]="imgRel" >
            <div class="card-body">
              <h4 class="card-title">{{ loggedUcenik.name }} {{ loggedUcenik.surname }}</h4>
              <p class = "card-text">Adresa:{{ loggedUcenik.adress }}</p>
              <p class = "card-text">Email:{{ loggedUcenik.email }}</p>
              <p class = "card-text">Telefon:{{ loggedUcenik.telephone }}</p>
              <p class = "card-text">Tip skole:{{ loggedUcenik.schooltype }}</p>
              <p class = "card-text">Razred:{{ loggedUcenik.schoolgrade }}</p>
            </div>

          </div>
        </div>
      </div>
      <hr>
      <h3>Uredi Profil</h3>
      <label for="name">Ime:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="name" [(ngModel)]="editedUcenik.name" >
      <br/>
      <label for="surname">Prezime:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="surname" [(ngModel)]="editedUcenik.surname" >
      <br/>
      <label for="adress">Adresa:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="adress" [(ngModel)]="editedUcenik.adress" >
      <br/>
      <label for="email">Email:</label>
      <input class = "btn-outline-dark rounded-pill" type="email" id="email" [(ngModel)]="editedUcenik.email" >
      <br/>
      <label for="telephone">Telefon:</label>
      <input class = "btn-outline-dark rounded-pill" type="tel" id="telephone" [(ngModel)]="editedUcenik.telephone" >
      <br/>
      <label for="schooltype">Tip škole:</label>
      <select class = "btn-outline-dark rounded-pill" [(ngModel)]="editedUcenik.schooltype">
        <option [value]="'osnovna1-4'" >Osnovna škola 1-4. razred</option>
        <option [value]="'osnovna5-8'" >Osnovna škola 5-8. razred</option>
        <option [value]="'srednja'" >Srednja škola</option>
      </select>
      <br/>

      <label for="schoolgrade">Trenutni razred:</label>
      <button class="btn btn-success" type="button" (click)="incrementSchoolGrade()">+</button>
      <br/>
      <label for="image">Nova slika:</label>
      <input class = "btn-outline-dark rounded-pill" type="file" (change)="onFileSelected($event)" />
      <br>
      <button class="btn btn-primary" type="button" (click)="saveChanges()">Sačuvaj</button>
      <br>


    </div>
    <div *ngIf="where === 2">
      <h3>Pretraga Nastavnika</h3>
      <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="searchIme" placeholder="Ime">
      <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="searchPrezime" placeholder="Prezime">
      <button class="btn btn-primary" (click) = "filterNastavnici()" >Search</button>
      <table class="table table-striped">
        <thead>
          <tr>
            <th>
              Ime
              <div class="btn-group">
                <button class="btn btn-success" (click)="sortByIme(true)">Ascending</button>
                <button class="btn btn-danger" (click)="sortByIme(false)">Descending</button>
              </div>
            </th>
            <th>
              Prezime
              <div class="btn-group">
                <button class="btn btn-success" (click)="sortByPrezime(true)">Ascending</button>
                <button class="btn btn-danger" (click)="sortByPrezime(false)">Descending</button>
              </div>
            </th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let nastavnik of nastavnici" [hidden]="nastavnik.visible">
            <td>
              {{ nastavnik.name }} {{ nastavnik.surname }}
              <br>
              <a [routerLink]="['/nastavnikDetails', nastavnik.username]">Details</a>
            </td>
          </tr>
        </tbody>
      </table>

    </div>
    <div *ngIf="where === 3">
      <h2>Odrzani casovi:</h2>
      <div class="table-responsive">
        <table class="table table-bordered table-striped">
          <thead class="thead-dark">
            <tr>
              <th>Datum i vreme pocetka</th>
              <th>Datum i vreme kraja</th>
              <th>Nastavnik</th>
              <th>Predmet</th>
              <th>Komentar</th>
            </tr>
          </thead>
          <tbody>
            <ng-container *ngFor="let cas of odrzani">
              <tr>
                <td>{{cas.datumvreme | date: 'MM-dd-yyyy HH:mm'}}</td>
                <td>{{cas.datumvremekraj | date: 'MM-dd-yyyy HH:mm'}}</td>
                <td>{{cas.nastavnik}}</td>
                <td>{{cas.predmet}}</td>
                <td>{{cas.komentar}}</td>
              </tr>
            </ng-container>
          </tbody>
        </table>
      </div>
      <br/>
      <h2>Casovi koji slede:</h2>
      <div class="table-responsive">
        <table class="table table-bordered table-striped">
          <thead class="thead-dark">
            <tr>
              <th>Datum i vreme pocetka</th>
              <th>Datum i vreme kraja</th>
              <th>Nastavnik</th>
              <th>Predmet</th>
              <th>Opis</th>
            </tr>
          </thead>
          <tbody>
            <ng-container *ngFor="let cas of neodrzani">
              <tr>
                <td>{{cas.datumvreme | date: 'MM-dd-yyyy HH:mm'}}</td>
                <td>{{cas.datumvremekraj | date: 'MM-dd-yyyy HH:mm'}}</td>
                <td>{{cas.nastavnik}}</td>
                <td>{{cas.predmet}}</td>
                <td>{{cas.opis}}</td>
              </tr>
            </ng-container>
          </tbody>
        </table>
      </div>
    </div>
  </main>
  <footer>
    <div class="footer-content">
      <p>PIA Projekat</p>
    </div>
  </footer>
</body>
