import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginservisService } from '../servisi/loginservis.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private router: Router, private servis : LoginservisService){}


  username: string = ""
  password: string = ""
  type : number = 0
  message : string = ""


  login(){
    this.message = ""
    if(this.username === "" && this.password === "" && this.type === 0){
      this.message = "Popunite sva polja"
      return
    }
    this.message = ""
    this.servis.login(this.username, this.password,this.type).subscribe(
      data=>{
        if(data === null) {
          this.message = "Podatci pogresno uneti"
        }
        else {
          if(this.type == 1){
            localStorage.setItem("logged", data.username)
            this.router.navigate(['ucenik'])
          }else{
            localStorage.setItem("logged", data.username)
            this.router.navigate(['nastavnik'])
          }

        }
      }
    )
  }




}

<body>
  <div class ="center-container">
    <div class="container">
      <h3>Log in</h3>
      <br/>
      Username:
      <br/>
      <input class = "btn-outline-dark rounded-pill"  type="text" [(ngModel)]="username" placeholder = "Username...">
      <br/>
      Password:
      <br/>
      <input class = "btn-outline-dark rounded-pill" type="password" [(ngModel)]="password" placeholder = "Password...">
      <br/>
      Tip:
      <br/>
      <select class = "btn-outline-dark rounded-pill" [(ngModel)]="type">
        <option value="1">Ucenik</option>
        <option value="2">Nastavnik</option>
      </select>
      <br/>
      <span style = "color:red">{{message}}</span>
      <br>
      <button class="btn btn-primary" (click)="login()">Login</button>
      <br/>
      <a routerLink="/register">Registracija ucenika</a>
      <br/>
      <a routerLink="/registernastavnik">Registracija nastavnika</a>
      <br/>
      <a routerLink="/change">Promeni lozinku</a>
      <br/>
      <a routerLink="/">Nazad</a>
      <br>

    </div>
  </div>
</body>
