import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Ucenik } from '../models/ucenik';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  constructor(private servis: LoginservisService) {}


  user: Ucenik = new Ucenik()
  gradeO : Number[] = [1,2,3,4,5,6,7,8]
  gradeH : Number[] = [1,2,3,4]
  selectedFile: File | null = null;
  image : string | undefined;
  showPic : number = 0


  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }



  register(){
    this.user.picture = this.selectedFile;
    const passwordRegex = /^(?=.*[A-Z])(?=.*[a-z]{3,})(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,10}$/;

    if (!passwordRegex.test(this.user.password)) {
      alert('Format sifre je los ispravi');
      return;
    }
    this.servis.register(this.user).subscribe(
      data=>{
        if(data.message=="ok") alert("Dodato")
      }
    )
  }


  uploadTest(){
    this.servis.uploadPicture(this.selectedFile!).subscribe(data=>{
      if(data){

        console.log('File uploaded successfully.');
        this.image = 'http://localhost:4000/uploads/' + data.filename;
        console.log(this.image)
      }
    })
  }

  show(){
    this.showPic = 1
  }
}

<body>
  <div class ="center-container">
    <div class ="container">
      Username: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.username">
      <br/>
      Password: <input class = "btn-outline-dark rounded-pill" type="password" [(ngModel)]="user.password">
      <br/>
      Firstname: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.name">
      <br/>
      Lastname: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.surname">
      <br/>
      Safety question: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.safetyq">
      <br/>
      Safety answer: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.safetya">
      <br/>
      Adress: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.adress">
      <br/>
      email: <input class = "btn-outline-dark rounded-pill" type="email" [(ngModel)]="user.email">
      <br/>
      Telephone: <input class = "btn-outline-dark rounded-pill" type="number" [(ngModel)]="user.telephone">
      <br/>
      <label for="pol">Select Gender:</label>
      <select class = "btn-outline-dark rounded-pill" id="pol" [(ngModel)]="user.sex" name="pol">
        <option value="M">Musko</option>
        <option value="Z">Zensko</option>
      </select>
      <br/>
      <label for="schoolType">School Type:</label>
      <select class = "btn-outline-dark rounded-pill" id="schoolType" [(ngModel)]="user.schooltype" name="schoolType">
        <option value="osnovna">Osnovna</option>
        <option value="srednja-gimnazija">Srednja(Gimnazija)</option>
        <option value="srednja-strucna">Srednja(Strucna)</option>
        <option value="srednja-umetnicka">Srednja(Umetnicka)</option>
      </select>
      <br/>
      <label for="currentGrade">Current Grade:</label>
      <select class = "btn-outline-dark rounded-pill" id="currentGrade" [(ngModel)]="user.schoolgrade" name="currentGrade">
        <option *ngFor="let grade of gradeH" [value]="grade">{{ grade }}</option>
      </select>
      <br/>
      Image:<input class = "btn-outline-dark rounded-pill" type="file" (change)="onFileSelected($event)" />
      <br/>
      <button class="btn btn-primary" (click)="register()">Register</button>
      <br>
      <br/>
      <button class="btn btn-primary" (click)="uploadTest()">Upload</button>
      <br>
      <br/>
      <button class="btn btn-primary" (click)="show()">Show</button>
      <br>
      <div *ngIf = "showPic == 1">
        <img [src]="image" alt="Uploaded Image">

      </div>
      <a routerLink="/login">Nazad</a>

    </div>
  </div>
</body>
