            baseChart
            class="chart"
            [options]="chartOptions"
            [data]="barChartData2"
            [type]="barChartType2"
            >
            </canvas>
          </div>
          <br/>
          <div id="chart-container">
            <canvas
            baseChart
            class="chart"
            [options]="chartOptions"
            [data]="barChartDataAge"
            [type]="barChartType2"
            >
            </canvas>
          </div>
        </div>
      </div>



      <div *ngIf="chart === 2">
        <div id="chart-container">
          <canvas
          baseChart
          class="chart"
          [options]="chartOptions"
          [data]="pieChartData"
          [type]="pieType"
          >
          </canvas>
        </div>
        <div id="chart-container">
          <canvas
          baseChart
          class="chart"
          [options]="chartOptions"
          [data]="pieChartData2"
          [type]="pieType"
          >
          </canvas>
        </div>
      </div>

      <div *ngIf="chart === 3">
        <div id="chart-container">
          <canvas
          baseChart
          class="chart"
          [options]="histogramOptions"
          [data]="histogramChart"
          [type]="barChartType2"
          >
          </canvas>
        </div>
      </div>

      <div *ngIf="chart === 4">
        <div id="chart-container">
          <canvas
          baseChart
          class="chart"
          [options]="lineChartOptions"
          [data]="lineChartData"
          [type]="lineChartType"
          >
          </canvas>
        </div>
      </div>




    </div>
    <div *ngIf="where === 2">
      <div class="row">
        <div class="col-sm-12">
          <h3>Pregled prijava nastavnika:</h3>
          <ng-container *ngFor="let nastavnik of nastavnici">
            <div class="card" style="width: 200px; margin-bottom: 20px;">
              <div class="card-body">
                <h4 class="card-title">{{ nastavnik.name }} {{ nastavnik.surname }}</h4>
                <p class = "card-text">Adresa:{{ nastavnik.adress }}</p>
                <p class = "card-text">Email:{{ nastavnik.email }}</p>
                <p class = "card-text">Telefon:{{ nastavnik.telephone }}</p>
                <p class = "card-text">Predmeti:<ng-container *ngFor="let predmet of nastavnik.predmeti">
                  <div class="indented">{{ predmet }}</div>
                </ng-container></p>
                <p class = "card-text">Razredi:<ng-container *ngFor="let razred of nastavnik.razredi">
                  <div class="indented">{{ razred }}</div>
                </ng-container></p>
                <div class="btn-group" role="group">
                  <button class="btn btn-success" type="button" (click)="prihvatiNastavnika(nastavnik.username)">Prihvati Nastavnika</button>
                  <button class="btn btn-danger" type="button" (click)="odbijNastavnika(nastavnik.username)">Odbij Nastavnika</button>
                </div>
              </div>
            </div>
          </ng-container>
        </div>
      </div>

      <div class="row">
        <div class="col-md-12">
          <h3>Pregled predmeta:</h3>
          <table class="table">
            <thead>
              <tr>
                <th>Predmet</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              <ng-container *ngFor="let prdmt of predmeti">
                <tr>
                  <td>{{ prdmt.predmet }}</td>
                  <td>
                    <button class="btn btn-success" type="button" (click)="addPredmet(prdmt.predmet)">Prihvati Predmet</button>
                  </td>
                  <td>
                    <button class="btn btn-danger" type="button" (click)="rejectPredmet(prdmt.predmet)">Odbij Predmet</button>
                  </td>
                </tr>
              </ng-container>
            </tbody>
          </table>
        </div>
      </div>
      <h3>Dodaj Predmet:</h3>
      <input  class = "btn-outline-dark rounded-pill" type ="text" [(ngModel)] = "predmettoadd">
      <button class="btn btn-primary" type="button" (click)="addPr(predmettoadd)" >DodajPredmet</button>

    </div>
  </main>
  <footer>
    <div class="footer-content">
      <p>PIA Projekat</p>
    </div>
  </footer>

</body>
