import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Nastavnik } from '../models/nastavnik';
import { Predmet } from '../models/predmet';
import { Router } from '@angular/router';

@Component({
  selector: 'app-registernastavnik',
  templateUrl: './registernastavnik.component.html',
  styleUrls: ['./registernastavnik.component.css']
})
export class RegisternastavnikComponent {

  constructor(private servis: LoginservisService,private router : Router) {}

  selectedAges: string[] = [];

  user: Nastavnik = new Nastavnik()

  firststep : number = 0
  predmeti : Predmet[] = []
  selectedPredmeti: string[] = []
  predmetToAdd : string = ""
  message: string = ""
  message2: string = ""
  selectedFile: File | null = null;
  selectedPDF: File | null = null;
  imageUrl: string = ""
  PDFUrl: string = ""

  ngOnInit(){
    this.imageUrl = ""
    this.PDFUrl = ""
    this.firststep = 0
    this.servis.getpredmeti(1).subscribe(data=>{
      if(data)this.predmeti = data

    })
  }

  goToSecondStep(){
    if(this.user.username === "" && this.user.name === "" && this.user.password === "" && this.user.surname === "" &&
    this.user.safetya === "" && this.user.safetyq === "" && this.user.adress === "" && this.user.email === "" && this.user.sex === "" && this.user.telephone === 0){
      this.message = "Popunite sva polja"
      return
    }
    this.message = ""
    const passwordRegex = /^(?=.*[A-Z])(?=.*[a-z]{3,})(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,10}$/;

    if (!passwordRegex.test(this.user.password)) {
      this.message = "Format sifre je los"
      return;
    }
    if(this.selectedFile !== null){
      this.uploadTest().then(() =>{
        this.user.profilePic = this.imageUrl
        alert(this.user.profilePic)
        this.firststep++;
      })

    }else{
      this.firststep++;
    }

  }

  toggleAge(age: string) {
    if (this.selectedAges.includes(age)) {
      // Ukloni uzrast ako već postoji u nizu
      this.selectedAges = this.selectedAges.filter(item => item !== age);
    } else {
      // Dodaj uzrast ako ne postoji u nizu
      this.selectedAges.push(age);
    }


  }

  showAlert() {
    this.message2 = ""
    this.user.razredi = this.selectedAges;
    this.user.predmeti = this.selectedPredmeti
    if(this.selectedPDF == null){
      this.message2 = "Morate CV staviti"

      return;
    }
    if (this.selectedPredmeti.length === 0) {
      this.message2 = "Nema izabranih predmeta"

      return;
    }
    if (this.selectedAges.length === 0) {
      this.message2 = "Nema izabranih uzrasta"
      return;
    } else {
      this.uploadPDF().then(() => {
        this.user.CV = this.PDFUrl
        this.servis.registerN(this.user).subscribe(
          data=>{
            if(data.message=="ok"){
              this.firststep = 0;
              this.router.navigate(['login'])
            }
          }
        )
        this.firststep = 0;
      })

    }

  }


  toggleSelection(predmet: string) {
    if (this.selectedPredmeti.includes(predmet)) {
      this.selectedPredmeti = this.selectedPredmeti.filter(p => p !== predmet);
    } else {
      this.selectedPredmeti.push(predmet);
    }
  }

  addPredmet(){
    if(this.predmetToAdd === ""){
      alert("Unesite Predmet")
    }else{
      this.servis.addpredmeti(this.predmetToAdd,0).subscribe(data=>{

      })
    }
  }

  onFileSelected(event: any) {
    if(event.target.files[0].type  === 'application/pdf'){
      this.selectedPDF = event.target.files[0];
    }else{
      this.selectedFile = event.target.files[0];
    }
    //this.selectedFile = event.target.files[0];
  }


  uploadTest(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      this.servis.uploadPicture(this.selectedFile!).subscribe(data => {
        if (data && data.filename) {
          console.log('File uploaded successfully.');
          this.imageUrl = data.filename;
          console.log(this.imageUrl);
          resolve(); // Resolve the Promise when upload is completed
        } else {
          reject(new Error('Upload failed')); // Reject the Promise if upload fails
        }
      });
    });
  }


  uploadPDF(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      this.servis.uploadPicture(this.selectedPDF!).subscribe(data => {
        if (data && data.filename) {
          console.log('File uploaded successfully.');
          this.PDFUrl = data.filename;
          console.log(this.imageUrl);
          resolve(); // Resolve the Promise when upload is completed
        } else {
          reject(new Error('Upload failed')); // Reject the Promise if upload fails
        }
      });
    });
  }

}


<body>
  <div class ="center-container">
    <div class ="container">
      <div *ngIf="firststep === 0">
        Username: <input  class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.username">
        <br/>
        Password: <input  class = "btn-outline-dark rounded-pill" type="password" [(ngModel)]="user.password">
        <br/>
        Firstname: <input  class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.name">
        <br/>
        Lastname: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.surname">
        <br/>
        Safety question: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.safetyq">
        <br/>
        Safety answer: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.safetya">
        <br/>
        Adress: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.adress">
        <br/>
        email: <input class = "btn-outline-dark rounded-pill" type="email" [(ngModel)]="user.email">
        <br/>
        Telephone: <input class = "btn-outline-dark rounded-pill" type="number" [(ngModel)]="user.telephone">
        <br/>
        <label for="pol">Select Gender:</label>
        <select class = "btn-outline-dark rounded-pill" id="pol" [(ngModel)]="user.sex" name="pol">
          <option value="M">Musko</option>
          <option value="Z">Zensko</option>
        </select>
        <br/>

        Image:<input class = "btn-outline-dark rounded-pill" type="file" (change)="onFileSelected($event)" />
        <br/>
        <button class="btn btn-primary" (click)="goToSecondStep()">Next</button>
        <br>
        <span style = "color:red">{{message}}</span>
        <br>
        <a routerLink="/login">Nazad</a>


      </div>
      <div *ngIf="firststep === 1">
        Razredi gde bi predavali:
        <br/>
        <label>
          <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('osnovna1-4')" (change)="toggleAge('osnovna1-4')">
          Osnovna škola 1-4. razred
        </label>
        <br/>
        <label>
          <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('osnovna5-8')" (change)="toggleAge('osnovna5-8')">
          Osnovna škola 5-8. razred
        </label>
        <br/>
        <label>
          <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('srednja')" (change)="toggleAge('srednja')">
          Srednja škola
        </label>
        <br/>
        Gde ste culi za nas sajt: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.referenca" >
        <br/>
        Koje predmete bi predavali?
        <br/>
        <label *ngFor="let predmet of predmeti">
          <input class = "rounded-pill" type="checkbox" name="predmeti" [value]="predmet.predmet" (change)="toggleSelection(predmet.predmet)">
          {{ predmet.predmet }}
          <br/>
        </label>
        <br/>
        Neki drugi predmet(unesite ime zeljenog predmeta ako ocete vise predmeta unesite 1 po 1)?
        <br/>
        <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="predmetToAdd">
        <button class="btn btn-primary" (click)="addPredmet()">Dodaj</button>
        <br/>
        PDF:<input class = "btn-outline-dark rounded-pill" type="file" (change)="onFileSelected($event)" />
        <br/>
        <span style = "color:red">{{message2}}</span>
        <br>

        <br/>
        <button  class="btn btn-primary" (click)="showAlert()">Register</button>
        <br>
        <a routerLink="/login">Nazad</a>
      </div>

    </div>
  </div>
</body>
