      // If no file is selected (file input field is empty), reset selectedPDF and return
      this.selectedPDF = null;
      return;
    }
    if (this.selectedPDF.size > 3 * 1024 * 1024) {
      this.selectedPDF = null;
      alert("CV ne sme imati vise od 3MB");
      return;
