    this.uploadPDF().then(() => {
      this.servis.updateCV(this.nastavnik.username, this.updatedpdfUrl).subscribe(rez => {});
      this.zaOpenPDF = this.updatedpdfUrl;
      this.pdfUrl = this.backend + this.updatedpdfUrl;
