import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginservisService } from '../servisi/loginservis.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private router: Router, private servis : LoginservisService){}


  username: string = ""
  password: string = ""


  login(){
    this.servis.login(this.username, this.password).subscribe(
      data=>{
        if(data==null) alert("Nema korisnika")
        else {
          localStorage.setItem("logged", data.username)
          this.router.navigate(['ucenik'])
        }
      }
    )
  }




}
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Log in</title>
  <link href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css" rel="stylesheet">
</head>
<body>
  <div class ="center-container">
    <div class="container">
      <h3>Log in</h3>
      <br/>
      Username:
      <br/>
      <input  type="text" [(ngModel)]="username">
      <br/>
      Password:
      <br/>
      <input  type="password" [(ngModel)]="password">
      <br/>
      <button class="btn btn-primary" (click)="login()">Login</button>
    </div>
  </div>
</body>
