import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Nastavnik } from '../models/nastavnik';
import { Predmet } from '../models/predmet';
import { Router } from '@angular/router';

import { ChartConfiguration, ChartData, ChartEvent, ChartType } from 'chart.js';
import { BaseChartDirective } from 'ng2-charts';
import { ChartDataset } from 'chart.js';

class barChartSubject{
  subject : string = "";
  count : number = 0;
}
class barChartAge{

}

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent {
  constructor(private servis :LoginservisService, private router : Router ){}
  where : number = 1
  chart : number = 0
  nastavnici : Nastavnik[] = []
  predmeti : Predmet[] = []
  predmetiGOOD : Predmet[] = []
  predmettoadd : string = ""
  ageGRP : string[] = ["osnovna","srednja-gimnazija","srednja-strucna","srednja-umetnicka"]
  nastavniciGOOD : Nastavnik[] = []
  //BarDiagramSubjects
  public barChartType2: ChartType = 'bar';
  public barChartData2: ChartData<'bar'> = {
    labels: ['2006', '2007', '2008', '2009', '2010', '2011', '2012'],
    datasets: [
      { data: [65, 59, 80, 81, 56, 55, 40], label: 'Series A' },
      { data: [28, 48, 40, 19, 86, 27, 90], label: 'Series B' },
    ],
  };
  public barChartLabels: string[] = [];
  public barChartType: ChartType = 'bar';
  public barChartLegend = true;
  public barChartData: any[] = [];
  subjectList : barChartSubject[] = []

  //BarDiagramSubjects
  public barChartLabelsAge: string[] = [];
  public barChartTypeAge = 'bar';
  public barChartLegendAge = true;
  public barChartDataAge: any[] = [];

  changeWhere(b : number){
    this.where = b;
  }

  ngOnInit(){
    this.servis.getAllNastavnik().subscribe(rez=>{
        this.nastavniciGOOD = rez

    })
    this.servis.nastavnikwait().subscribe(rez =>{
       this.nastavnici = rez
    })
    this.servis.getpredmeti(0).subscribe(rez=>{
      this.predmeti = rez
    })
    this.servis.getpredmeti(1).subscribe(rez=>{
      this.predmetiGOOD = rez
    })
    this.servis.getpredmeti(1).subscribe(rez=>{
      if(rez){
        this.servis.getAllNastavnik().subscribe(data=>{
          if(data){
            this.countBySubject(rez,data)
            this.populateBarChartSubject()
          }

      })
      }
    })


  }

  prihvatiNastavnika(name : string){
    this.servis.potvrdinastavnikagain(name,1).subscribe(rez=>{
    })
    this.servis.nastavnikwait().subscribe(rez =>{
      this.nastavnici = rez
    })
  }

  odbijNastavnika(name : string){
    this.servis.potvrdinastavnikagain(name,2).subscribe(rez=>{

    })
    this.servis.nastavnikwait().subscribe(rez =>{
      this.nastavnici = rez
    })
  }

  addPredmet(name : string){
    this.servis.potvrdipredmet(name,1).subscribe(rez=>{

    })
    this.servis.getpredmeti(0).subscribe(rez=>{
      this.predmeti = rez
    })
  }

  rejectPredmet(name : string){
    this.servis.potvrdipredmet(name,2).subscribe(rez=>{

    })
    this.servis.getpredmeti(0).subscribe(rez=>{
      this.predmeti = rez
    })
  }

  addPr(name : string){
    this.servis.addpredmeti(name,1).subscribe(rez=>{

    })
    this.ngOnInit()
  }


  LogOut(){
    localStorage.clear
    this.router.navigate([''])
  }
  //BAR DIAGRAM FUNC
  countBySubject(pred: Predmet[],nast : Nastavnik[]){
    pred.forEach(subject=>{
      let count = nast.filter(teacher => teacher.predmeti.includes(subject.predmet)).length
      const hlp = new barChartSubject()
      hlp.count = count
      hlp.subject = subject.predmet
      this.subjectList.push(hlp)
    })
  }

  populateBarChartSubject(){
    const newDataset: { data: number[], label: string } = { data: [], label: 'Broj nastavnika po predmetu' };
    //if (!this.barChartData2.labels) {
    //  this.barChartData2.labels = [];
   // }
    this.subjectList.forEach(data=>{
     // if(this.barChartData2.labels){
     //   this.barChartData2.labels.push(data.subject)
    //  }
      newDataset.data.push(data.count)
      this.barChartLabels.push(data.subject)
      this.barChartData.push({ data: [data.count], label: data.subject })
    })

    //this.barChartLabels.push(subjectData.subject);
    //this.barChartData.push({ data: [subjectData.teacherCount], label: subjectData.subject });
  }
  showChartBar(){
    this.chart = 1

  }


}
<header class="navbar navbar-dark-grey">
  <div class="container">
    <div class="navbar-right">
      <span (click)="changeWhere(1)">Dijagrami  </span>
      <span (click)="changeWhere(2)">Funkcije  </span>
      <span (click)="LogOut()">Log out</span>
    </div>
  </div>

</header>
<div *ngIf="where === 1">
  <canvas
  baseChart
  class="chart"
  [data]="barChartData2"
  [type]="barChartType2"
  >
  </canvas>


</div>
<div *ngIf="where === 2">
  <div class="row">
    <div class="col-md-12">
      <h3>Pregled prijava nastavnika:</h3>
      <table class="table">
        <thead>
          <tr>
            <th>Nastavnik</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          <ng-container *ngFor="let nst of nastavnici">
            <tr>
              <td>{{ nst.username }}</td>
              <td>
                <button class="btn btn-success" type="button" (click)="prihvatiNastavnika(nst.username)">Prihvati Nastavnika</button>
              </td>
              <td>
                <button class="btn btn-danger" type="button" (click)="odbijNastavnika(nst.username)">Odbij Nastavnika</button>
              </td>
            </tr>
          </ng-container>
        </tbody>
      </table>
    </div>
  </div>

  <div class="row">
    <div class="col-md-12">
      <h3>Pregled predmeta:</h3>
      <table class="table">
        <thead>
          <tr>
            <th>Predmet</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          <ng-container *ngFor="let prdmt of predmeti">
            <tr>
              <td>{{ prdmt.predmet }}</td>
              <td>
                <button class="btn btn-success" type="button" (click)="addPredmet(prdmt.predmet)">Prihvati Predmet</button>
              </td>
              <td>
                <button class="btn btn-danger" type="button" (click)="rejectPredmet(prdmt.predmet)">Odbij Predmet</button>
              </td>
            </tr>
          </ng-container>
        </tbody>
      </table>
    </div>
  </div>
  <h3>Dodaj Predmet:</h3>
  <input  class = "btn-outline-dark rounded-pill" type ="text" [(ngModel)] = "predmettoadd">
  <button class="btn btn-primary" type="button" (click)="addPr(predmettoadd)" >DodajPredmet</button>

</div>
