import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { LoginservisService } from '../servisi/loginservis.service';
import { Nastavnik } from '../models/nastavnik';
import { Cas } from '../models/cas';
import { Router } from '@angular/router';

@Component({
  selector: 'app-nastavnikdetalji',
  templateUrl: './nastavnikdetalji.component.html',
  styleUrls: ['./nastavnikdetalji.component.css']
})
export class NastavnikdetaljiComponent {
  constructor(private route: ActivatedRoute, private servis : LoginservisService, private ruter : Router) {}
  username : string = ""
  nastavnik : Nastavnik = new Nastavnik()
  cas : Cas = new Cas()
  casovi : Cas[] = []
  isSpecialized : number = 0
  dupli : boolean = false
  message : string = ""

  ngOnInit(){

    this.message=""
    this.isSpecialized  = 0
    this.username = this.route.snapshot.paramMap.get('username')!;
    this.cas.nastavnik = this.username
    this.cas.ucenik = localStorage.getItem("logged")!
    this.servis.getnastavnik(this.username).subscribe(nst=>{
      this.nastavnik = nst
      if(this.nastavnik.predmeti.length == 1){
        this.isSpecialized  = 1
      }
    })
    this.servis.getCasoviNastavnika(this.username,1).subscribe(data=>{
      if(data){
        this.casovi = data
        for(let b of this.casovi){


        }
      }

    })



  }




  submitForm() {
    let novi = new Date(this.cas.datumvreme);
    let begin = new Date(this.cas.datumvreme);
    if(this.cas.predmet === ""){
      this.message = "Izaberite predmet"
      return;
    }

    if(this.isWeekend(begin)){
      this.message = "Dont't work on weekends"
      return;
    }
    if(this.dupli){
      novi.setMinutes(novi.getMinutes() + 120);
    }else{
      novi.setMinutes(novi.getMinutes() + 60);
    }
    if(begin.getHours() < 10){
      this.message = "Start time is before 10 AM"
      return;
    }

    if(novi.getHours() >= 18){
      this.message = "End time is after 6 PM"
      return;
    }
    if(this.checkForOverlap(begin,novi)){
      this.message = "Vec postoji cas u tom terminu"
      return;
    }
    novi.setHours(novi.getHours() + 1);
    begin.setHours(begin.getHours() + 1);
    this.message=""
    this.cas.datumvreme = begin
    this.cas.datumvremekraj = novi;

    this.servis.createcas(this.cas).subscribe(data=>{
      alert(data.message)
    })
    this.servis.getCasoviNastavnika(this.username,1).subscribe(data=>{
      if(data){
        this.casovi = data
      }

    })

  }

  checkForOverlap(begin: Date, novi: Date): boolean {

    const beginTime = begin.getTime();
    const noviTime = novi.getTime();

    for (let cas of this.casovi) {
      const casDatumvreme = new Date(cas.datumvreme).getTime();
      const casDatumvremekraj = new Date(cas.datumvremekraj).getTime();

      if (
        (beginTime >= casDatumvreme && beginTime < casDatumvremekraj) ||
        (noviTime > casDatumvreme && noviTime <= casDatumvremekraj) ||
        (beginTime <= casDatumvreme && noviTime >= casDatumvremekraj)
      ) {
        return true;
      }
    }

    return false;
  }

  isWeekend(date: Date): boolean {
    const day = date.getDay();
    return day === 0 || day === 6;
  }

  back(){
    this.ruter.navigate(['ucenik'])
  }



}

<h2>Profil</h2>
<table class="table table-striped">
  <tbody>
    <tr>
      <th scope="row">Ime</th>
      <td>{{ nastavnik.name }}</td>
    </tr>
    <tr>
      <th scope="row">Prezime</th>
      <td>{{ nastavnik.surname }}</td>
    </tr>
    <tr>
      <th scope="row">Adresa</th>
      <td>{{ nastavnik.adress }}</td>
    </tr>
    <tr>
      <th scope="row">Email</th>
      <td>{{ nastavnik.email }}</td>
    </tr>
    <tr>
      <th scope="row">Telefon</th>
      <td>{{ nastavnik.telephone }}</td>
    </tr>
    <tr>
      <th scope="row">Predmeti</th>
      <td>
        <ul class="list-unstyled">
          <ng-container *ngFor="let predmet of nastavnik.predmeti">
            <li>{{ predmet }}</li>
          </ng-container>
        </ul>
      </td>
    </tr>
  </tbody>
</table>
<br/>

<h2>Zakazivanje casa:</h2>
<form (ngSubmit)="submitForm()" #classForm="ngForm" novalidate>
  <label for="subject">Predmet:</label>
  <select class = "btn-outline-dark rounded-pill" id="subject" name="subject" [(ngModel)]="cas.predmet" required>
    <ng-container *ngIf="isSpecialized; else multipleSubjects">
      <option [value]="nastavnik.predmeti[0]" disabled>{{ nastavnik.predmeti[0] }}</option>
    </ng-container>


    <ng-template #multipleSubjects>
      <option *ngFor="let predmet of nastavnik.predmeti" [value]="predmet">{{ predmet }}</option>
    </ng-template>
  </select>
  <br/>


  <label for="dateTime">Datum i vreme:</label>
  <input class = "btn-outline-dark rounded-pill" id="dateTime" name="dateTime" type="datetime-local" [(ngModel)]="cas.datumvreme" required>
  <br/>

  <label for="description">Opis:</label>
  <textarea class = "btn-outline-dark rounded-pill" id="description" name="description" [(ngModel)]="cas.opis" required></textarea>
  <br/>

  <label for="doubleClass">Dupli cas:</label>
  <input class = "btn-outline-dark rounded-pill" id="doubleClass" name="doubleClass" type="checkbox" [(ngModel)]="dupli">
  <br/>
  <span style = "color: red" *ngIf="message !== ''">{{message}}</span>
  <br>
  <button class="btn btn-primary" type="submit">Submit</button>
</form>

<br>
<button class="btn btn-primary"(click) = "back()">Nazad</button>
<br>

