import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Ucenik } from '../models/ucenik';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  constructor(private servis: LoginservisService, private router: Router) {}


  user: Ucenik = new Ucenik()
  gradeO : Number[] = [1,2,3,4,5,6,7,8]
  gradeH : Number[] = [1,2,3,4]
  selectedFile: File | null = null;
  image : string | undefined;
  showPic : number = 0
  imageUrl: string = ""
  pdfUrl: string = ""
  message: string = ""


  onFileSelected(event: any): void {
    this.selectedFile = event.target.files[0];
    const fileReader = new FileReader();

    if (! this.selectedFile) {
      // If no file is selected (file input field is empty), set showPic to 0
      this.showPic = 0;
      this.selectedFile = null; // Clear the selected file
      return;
    }

    fileReader.onload = (e) => {
      const img = new Image();
      img.src = fileReader.result as string;
      img.onload = () => {
        const width = img.width;
        const height = img.height;
        if (width >= 100 && width <= 300 && height >= 100 && height <= 300) {
          console.log("Image dimensions are valid:", width, "x", height);
          this.message = ""
          this.showPic = 1; // Optionally show the image preview
        } else {
          this.message = "Slika mora biti u regionu 100x100 i 300x300";
          this.showPic = 2; // Optionally hide the image preview
          this.selectedFile = null; // Clear the selected file
          return

        }
      };
    };
    if(this.showPic === 2)return
    fileReader.readAsDataURL(this.selectedFile!);
  }



  register(){
    if(this.showPic === 2){
      this.message = "Slika je loseg formata"
      return
    }
    if(this.selectedFile !== null){
      if(this.user.username === "" && this.user.name === "" && this.user.password === "" && this.user.surname === "" &&
        this.user.safetya === "" && this.user.safetyq === "" && this.user.adress === "" && this.user.email === "" && this.user.sex === "" && this.user.telephone === "" &&
        this.user.sex === "" && this.user.schooltype === "" && this.user.schoolgrade === 0 ){
          this.message = "Popunite sva polja"
          return
        }
        this.message = ""
        //this.user.picture = this.selectedFile;
        const passwordRegex = /^(?=.*[A-Z])(?=.*[a-z]{3,})(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,10}$/;

        if (!passwordRegex.test(this.user.password)) {
          this.message = "Format sifre je los"
          return;
        }
      this.uploadTest().then(() => {
        this.user.profilePic = this.imageUrl
        this.servis.register(this.user).subscribe(
          data=>{
            if(data.message=="ok"){

              this.router.navigate(['login'])
            }else{
              console.log(data.message)
              this.message = data.message
            }
          }
        )

      });
      return

    }else{
      this.user.profilePic = "/uploads/default.jpg"
    }



    if(this.user.username === "" && this.user.name === "" && this.user.password === "" && this.user.surname === "" &&
    this.user.safetya === "" && this.user.safetyq === "" && this.user.adress === "" && this.user.email === "" && this.user.sex === "" && this.user.telephone === "" &&
    this.user.sex === "" && this.user.schooltype === "" && this.user.schoolgrade === 0 ){
      this.message = "Popunite sva polja"
      return
    }
    this.message = ""
    //this.user.picture = this.selectedFile;
    const passwordRegex = /^(?=.*[A-Z])(?=.*[a-z]{3,})(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,10}$/;

    if (!passwordRegex.test(this.user.password)) {
      this.message = "Format sifre je los"
      return;
    }
    //alert(this.user.profilePic)
    this.servis.register(this.user).subscribe(
      data=>{
        if(data.message=="ok"){

          this.router.navigate(['login'])
        }else{
          console.log(data.message)
          this.message = data.message
        }
      }
    )
  }


  uploadTest(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      this.servis.uploadPicture(this.selectedFile!).subscribe(data => {
        if (data && data.filename) {
          console.log('File uploaded successfully.');
          this.imageUrl = data.filename;
          console.log(this.imageUrl);
          resolve(); // Resolve the Promise when upload is completed
        } else {
          reject(new Error('Upload failed')); // Reject the Promise if upload fails
        }
      });
    });
  }

  show(){
    this.showPic = 1
  }


  fetchPdf(){
    const filename = '1707046306323.pdf'; // Replace with the actual filename
    this.servis.getPdfUrl(filename).subscribe(
      (response: Blob) => {
        this.pdfUrl = URL.createObjectURL(response);
        console.log(this.pdfUrl)
      },
      (error) => {
        console.error('Error fetching PDF:', error);
      }
    );
  }


}

<body>
  <div class ="center-container">
    <div class ="container">
      Username: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.username">
      <br/>
      Password: <input class = "btn-outline-dark rounded-pill" type="password" [(ngModel)]="user.password">
      <br/>
      Firstname: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.name">
      <br/>
      Lastname: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.surname">
      <br/>
      Safety question: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.safetyq">
      <br/>
      Safety answer: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.safetya">
      <br/>
      Adress: <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="user.adress">
      <br/>
      email: <input class="btn-outline-dark rounded-pill" type="email" [(ngModel)]="user.email" name="email" required>
      <div *ngIf="email.invalid && (email.dirty || email.touched)">
        <div *ngIf="email.errors.required">
          Email is required.
        </div>
        <div *ngIf="email.errors.email">
          Please enter a valid email address.
        </div>
      </div>
      <br/>
      Telephone: <input class = "btn-outline-dark rounded-pill" type="tel" [(ngModel)]="user.telephone">
      <br/>
      <label for="pol">Select Gender:</label>
      <select class = "btn-outline-dark rounded-pill" id="pol" [(ngModel)]="user.sex" name="pol">
        <option value="M">Musko</option>
        <option value="Z">Zensko</option>
      </select>
      <br/>
      <label for="schoolType">School Type:</label>
      <select class = "btn-outline-dark rounded-pill" id="schoolType" [(ngModel)]="user.schooltype" name="schoolType">
        <option value="osnovna">Osnovna</option>
        <option value="srednja-gimnazija">Srednja(Gimnazija)</option>
        <option value="srednja-strucna">Srednja(Strucna)</option>
        <option value="srednja-umetnicka">Srednja(Umetnicka)</option>
      </select>
      <br/>
      <label for="currentGrade">Current Grade:</label>
      <select *ngIf="user.schooltype !== 'osnovna'" class = "btn-outline-dark rounded-pill" id="currentGrade" [(ngModel)]="user.schoolgrade" name="currentGrade">
        <option *ngFor="let grade of gradeH" [value]="grade">{{ grade }}</option>
      </select>
      <select *ngIf="user.schooltype === 'osnovna'" class = "btn-outline-dark rounded-pill" id="currentGrade" [(ngModel)]="user.schoolgrade" name="currentGrade">
        <option *ngFor="let grade of gradeO" [value]="grade">{{ grade }}</option>
      </select>
      <br/>
      Profilna slika:<input class = "btn-outline-dark " type="file" (change)="onFileSelected($event)" accept="image/*"  />
      <br/>
      <button class="btn btn-primary" (click)="register()">Register</button>
      <br>
      <span style = "color:red">{{message}}</span>
      <br>




      <a routerLink="/login">Nazad</a>

    </div>
  </div>
</body>
