import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Ucenik } from '../models/ucenik';
import { Nastavnik } from '../models/nastavnik';
import { Cas } from '../models/cas';

class NastavnikInfo{
  ime: string = "";
  prezime : string = "";
  predmet: string = "";
  visible : boolean = true;



  constructor(ime: string, prezime: string, predmet: string) {
    this.ime = ime;
    this.prezime = prezime;
    this.predmet = predmet;
  }
}

@Component({
  selector: 'app-guest',
  templateUrl: './guest.component.html',
  styleUrls: ['./guest.component.css']
})
export class GuestComponent {
  constructor(private servis : LoginservisService){}
  ucenici : Ucenik[] = []
  nastavnici : Nastavnik[] = []
  casovi : Cas[] = []
  casovi7 : Cas[] = []
  casovi31 : Cas[] = []
  tableainfo : NastavnikInfo[] = []
  tableinfoHolder : NastavnikInfo[] = []
  searchIme: string = '';
  searchPrezime: string = '';
  searchPredmet: string = '';
  imageUrl: string = ""

  ngOnInit(){

    this.servis.getAllCasoviOdrzani().subscribe(data =>{
      if(data){
        this.casovi = data
      }
    })
    this.servis.getCasoviMonth().subscribe(data=>{
      if(data){
        this.casovi31 = data
        for(let b of this.casovi31){
          console.log(b.datumvreme)
        }
      }

    })
    this.servis.getCasoviSeven().subscribe(data=>{
      if(data)this.casovi7 = data
    })
    this.servis.getAllUcenici().subscribe(data => {
      if(data)this.ucenici = data
    })
    this.servis.getAllNastavnik().subscribe(data=>{
      this.nastavnici = data
      this.nastavnici.forEach((nastavnik) =>{
        nastavnik.predmeti.forEach((predmet) =>{
          this.tableainfo.push(new NastavnikInfo(nastavnik.name, nastavnik.surname, predmet));
        }

        )
      })
      this.tableinfoHolder = this.tableainfo


    })
  }

  sortByPrezime(ascending: boolean){
    this.tableainfo.sort((a, b) => {
      const nameA = a.prezime.toLowerCase();
      const nameB = b.prezime.toLowerCase();
      if (ascending) {
        return nameA.localeCompare(nameB);
      } else {
        return nameB.localeCompare(nameA);
      }
    });
  }


  sortByIme(ascending: boolean){
    this.tableainfo.sort((a, b) => {
      const nameA = a.ime.toLowerCase();
      const nameB = b.ime.toLowerCase();
      if (ascending) {
        return nameA.localeCompare(nameB);
      } else {
        return nameB.localeCompare(nameA);
      }
    });
  }


  sortByPredmet(ascending: boolean){
    this.tableainfo.sort((a, b) => {
      const nameA = a.predmet.toLowerCase();
      const nameB = b.predmet.toLowerCase();
      if (ascending) {
        return nameA.localeCompare(nameB);
      } else {
        return nameB.localeCompare(nameA);
      }
    });
  }


  filterNastavnici(){
    this.tableainfo.forEach(nastavnik => {
      const imeMatch = this.searchIme ? nastavnik.ime.toLowerCase().includes(this.searchIme.toLowerCase()) : true;
      const prezimeMatch = this.searchPrezime ? nastavnik.prezime.toLowerCase().includes(this.searchPrezime.toLowerCase()) : true;
      const predmetMatch = this.searchPredmet ? nastavnik.predmet.toLowerCase().includes(this.searchPredmet.toLowerCase()) : true;

      nastavnik.visible = imeMatch && prezimeMatch && predmetMatch;
    });
  }


  fetchImage(): void {
    const filename = '1707042431780.jpg'; // Replace with the actual filename of the image
    this.servis.getImage(filename)
      .subscribe({
        next: (response: Blob) => {
          const reader = new FileReader();
          reader.onloadend = () => {
            this.imageUrl = reader.result as string;
          };
          reader.readAsDataURL(response);
        },
        error: (error) => {
          console.error('Error fetching image:', error);
        }
      });
  }






}




<body>
  <div class ="center-container">
    <div class="container ">
      <table class="table table-bordered">
        <thead class="thead-light">
            <tr>
              <th class = "col-sm-3">Broj ucenika</th>
              <th class = "col-sm-3">Broj aktivnih nastavnika</th>
              <th class = "col-sm-3">Broj odrzanih casova u zadnjih 7 dana</th>
              <th class = "col-sm-3">Broj odrzanih casova u zadnjih mesec dana</th>
            </tr>
        </thead>
        <tbody>
          <tr>
            <td>{{ ucenici.length }}</td>
            <td>{{ nastavnici.length }}</td>
            <td>{{ casovi7.length }}</td>
            <td>{{ casovi31.length }}</td>
          </tr>
        </tbody>
      </table>
      <br/>
      <a class="btn btn-primary mt-3" routerLink="/login">LogIn/Register</a>
      <h3>Lista nastavnika i predmeta</h3>
      <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="searchIme" placeholder="Ime">
      <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="searchPrezime" placeholder="Prezime">
      <input class = "btn-outline-dark rounded-pill" type="text" [(ngModel)]="searchPredmet" placeholder="Predmet">
      <button class="btn btn-primary" (click) = "filterNastavnici()" >Search</button>
      <br/>
      <table class="table table-striped">
        <thead>
          <tr>
            <th class = "col-sm-4">
              Ime
              <div class="btn-group">
                <button class="btn btn-success" (click)="sortByIme(true)">Ascending</button>
                <button class="btn btn-danger" (click)="sortByIme(false)">Descending</button>
              </div>
            </th>
            <th class = "col-sm-4">
              Prezime
              <div class="btn-group">
                <button class="btn btn-success" (click)="sortByPrezime(true)">Ascending</button>
                <button class="btn btn-danger" (click)="sortByPrezime(false)">Descending</button>
              </div>
            </th>
            <th class = "col-sm-4">
              Predmet
              <div class="btn-group">
                <button class="btn btn-success" (click)="sortByPredmet(true)">Ascending</button>
                <button class="btn btn-danger" (click)="sortByPredmet(false)">Descending</button>
              </div>
            </th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let l of tableainfo" [hidden]="!l.visible">
            <td class = "col-sm-4">{{ l.ime }}</td>
            <td class = "col-sm-4">{{ l.prezime }}</td>
            <td class = "col-sm-4">{{ l.predmet }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</body>
