import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginservisService } from '../servisi/loginservis.service';

@Component({
  selector: 'app-admin-log-in',
  templateUrl: './admin-log-in.component.html',
  styleUrls: ['./admin-log-in.component.css']
})
export class AdminLogInComponent {
  constructor(private router: Router, private servis : LoginservisService){}
  username: string = ""
  password: string = ""
  message : string = ""

  login(){
    this.message = ""
    if(this.username === "" && this.password === "" ){
      this.message = "Popunite sva polja"
      return
    }
    this.servis.login(this.username,this.password,3).subscribe(data=>{
      if(data === null){
        this.message = "Username ili lozinka su pogresni"
      }else{
        localStorage.setItem("logged", data.username)
        this.router.navigate(['admin'])
      }
    })
  }

}

<body>
  <div class ="center-container">
    <div class="container">
      <h3>ADMIN Log in</h3>
      <br/>
      Username:
      <br/>
      <input class = "btn-outline-dark rounded-pill"  type="text" [(ngModel)]="username" placeholder = "Username...">
      <br/>
      Password:
      <br/>
      <input class = "btn-outline-dark rounded-pill" type="password" [(ngModel)]="password" placeholder = "Password...">
      <br/>
      <button class="btn btn-primary" (click)="login()">Login</button>
      <br/>
    </div>
  </div>
</body>
