import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Nastavnik } from '../models/nastavnik';
import { Cas } from '../models/cas';
import { Helper } from '../models/helper';
import { Router } from '@angular/router';
import { Predmet } from '../models/predmet';


@Component({
  selector: 'app-nastavnik',
  templateUrl: './nastavnik.component.html',
  styleUrls: ['./nastavnik.component.css']
})
export class NastavnikComponent {
  constructor(private servis : LoginservisService, private router : Router ) {}

  where : number = 1
  username : string = "nastavnik3"
  nastavnik : Nastavnik = new Nastavnik()
  edited : Nastavnik = new Nastavnik()
  selectedAges: string[] = [];
  casthree : Cas[] = []
  firstFiveElements: Cas[] = []
  casovizapotvrdu : Cas[] = []
  casovizaucenike : Cas[] = []
  ucenici : string[] = []
  ucenicinodupes : string[] = []
  helper : Helper[] = []
  predmeti : Predmet[] = []
  selectedPredmeti: string[] = []

  ngOnInit(){
    this.where = 1
    this.username = "nastavnik3"
    this.servis.getpredmeti(1).subscribe(data=>{
      if(data)this.predmeti = data

    })
    this.servis.getnastavnik(this.username).subscribe(nst=>{
      this.nastavnik = nst
    })
    this.selectedAges = []
    this.edited = new Nastavnik()
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi


    })
    this.servis.getsvojeucenike(this.username).subscribe(casovi=>{
      this.casovizaucenike = casovi
      for (let cas of this.casovizaucenike){
        this.ucenici.push(cas.ucenik)
      }
      this.ucenicinodupes = Array.from(new Set(this.ucenici))
      for (let ucenik of this.ucenicinodupes){
        let hlp = new Helper()
        hlp.ucenik = ucenik


        this.helper.push(hlp)
      }

    })





  }
  getAll(name : Helper){
    if(name.toggle){
      name.toggle = 0
    }else{

      name.toggle = 1
    }

  }


  changeWhere(b : number){
    this.where = b;
    if(b == 3){
      for(let hlp of this.helper){
        this.servis.getdosije(hlp.ucenik,this.username).subscribe(casovi=>{
          hlp.casovi = casovi
        })
      }
    }
  }


  saveChanges(){
    if(this.selectedPredmeti.length === 0){
      this.edited.predmeti = this.nastavnik.predmeti
    }else{
      this.edited.predmeti = this.selectedPredmeti
    }
    this.edited.username = this.nastavnik.username
    if(this.edited.name  === "")this.edited.name = this.nastavnik.name
    if(this.edited.surname  === "")this.edited.surname = this.nastavnik.surname
    if(this.edited.adress  === "")this.edited.adress = this.nastavnik.adress
    if(this.edited.email  === "")this.edited.email = this.nastavnik.email
    if(this.edited.telephone  === 0)this.edited.telephone = this.nastavnik.telephone
    if(this.selectedAges.length === 0){
      this.edited.razredi = this.nastavnik.razredi
    }else{
      this.edited.razredi = this.selectedAges
    }
    this.servis.updatenastavnik(this.edited).subscribe(data=>{

    })
    this.servis.getnastavnik(this.username).subscribe(nst=>{
      this.nastavnik = nst
    })
  }

  toggleAge(age: string) {
    if (this.selectedAges.includes(age)) {

      this.selectedAges = this.selectedAges.filter(item => item !== age);
    } else {
      // Dodaj uzrast ako ne postoji u nizu
      this.selectedAges.push(age);
    }


  }


  prihvatiCas(datum : Date){
    this.servis.potvrdiCas(datum,this.username,1).subscribe(data =>{

    })
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi

    })




  }

  odbijCas(datum : Date){
    this.servis.potvrdiCas(datum,this.username,2).subscribe(data =>{
      alert(data.message)
    })
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi

    })

  }

  odrziCas(datum : Date){
    this.servis.odrziCas(datum,this.username,1).subscribe(data =>{
      alert(data.message)
    })
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi

    })

  }

  LogOut(){
    localStorage.clear
    this.router.navigate([''])
  }

  toggleSelection(predmet: string) {
    if (this.selectedPredmeti.includes(predmet)) {
      this.selectedPredmeti = this.selectedPredmeti.filter(p => p !== predmet);
    } else {
      this.selectedPredmeti.push(predmet);
    }
  }
}
<header class="navbar navbar-dark-grey">
  <div class="container">
    <div class="navbar-right">
      <span (click)="changeWhere(1)">Profil  </span>
      <span (click)="changeWhere(2)">Casovi  </span>
      <span (click)="changeWhere(3)">Moji ucenici  </span>
      <span (click)="LogOut()">Log out</span>
    </div>
  </div>

</header>



<body>


  <main>
    <div *ngIf="where === 1">
      <div class="row">
        <div class="col-md-12">
          <h2>Profil</h2>
          <table class="table table-striped">
            <tbody>
              <tr>
                <th scope="row">Ime</th>
                <td>{{ nastavnik.name }}</td>
              </tr>
              <tr>
                <th scope="row">Prezime</th>
                <td>{{ nastavnik.surname }}</td>
              </tr>
              <tr>
                <th scope="row">Adresa</th>
                <td>{{ nastavnik.adress }}</td>
              </tr>
              <tr>
                <th scope="row">Email</th>
                <td>{{ nastavnik.email }}</td>
              </tr>
              <tr>
                <th scope="row">Telefon</th>
                <td>{{ nastavnik.telephone }}</td>
              </tr>
              <tr>
                <th scope="row">Predmeti</th>
                <td>
                  <ng-container *ngFor="let predmet of nastavnik.predmeti">
                    <div class="indented">{{ predmet }}</div>
                  </ng-container>
                </td>
              </tr>
              <tr>
                <th scope="row">Razredi</th>
                <td>
                  <ng-container *ngFor="let razred of nastavnik.razredi">
                    <div class="indented">{{ razred }}</div>
                  </ng-container>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
      <hr>
      <h3>Uredi Profil</h3>
      <label for="name">Ime:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="name" [(ngModel)]="edited.name" >
      <br/>

      <label for="surname">Prezime:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="surname" [(ngModel)]="edited.surname" >
      <br/>

      <label for="adress">Adresa:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="adress" [(ngModel)]="edited.adress" >
      <br/>

      <label for="email">Email:</label>
      <input class = "btn-outline-dark rounded-pill" type="email" id="email" [(ngModel)]="edited.email" >
      <br/>

      <label for="telephone">Telefon:</label>
      <input class = "btn-outline-dark rounded-pill" type="tel" id="telephone" [(ngModel)]="edited.telephone" >
      <br/>
      <p>Razredi(izaberite sve razrede kojima biste predavali):</p>
      <label>
        <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('osnovna1-4')" (change)="toggleAge('osnovna1-4')">
        Osnovna škola 1-4. razred
      </label>
      <br/>
      <label>
        <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('osnovna5-8')" (change)="toggleAge('osnovna5-8')">
        Osnovna škola 5-8. razred
      </label>
      <br/>
      <label>
        <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('srednja')" (change)="toggleAge('srednja')">
        Srednja škola
      </label>
      <br/>
      Koje predmete bi predavali?
      <br/>
      <div class="checkbox-container">
        <label *ngFor="let predmet of predmeti">
          <input class="btn-outline-dark rounded-pill" type="checkbox" name="predmeti" [value]="predmet.predmet" (change)="toggleSelection(predmet.predmet)">
          {{ predmet.predmet }}
        </label>
      </div>
      <br/>

      <button class="btn btn-primary" type="button" (click)="saveChanges()">Sačuvaj</button>


    </div>
    <div *ngIf="where === 2">
      <h3>Tabela zahteva</h3>
      <table>
        <tr>
          <td>
            Ucenik
          </td>
          <td>
            Predmet
          </td>
          <td>
            Opis
          </td>
          <td>
            Prihvati
          </td>
          <td>
            Odbij
          </td>
        </tr>
        <ng-container *ngFor="let zahtev of casovizapotvrdu">
          <tr>
            <td>
              {{ zahtev.ucenik }}
            </td>
            <td>
              {{ zahtev.predmet }}
            </td>
            <td>
              {{ zahtev.opis }}
            </td>
            <td>
              <button class="btn btn-success" type="button" (click)="prihvatiCas(zahtev.datumvreme)">Prihvati</button>
            </td>
            <td>
              <button class="btn btn-danger" type="button" (click)="odbijCas(zahtev.datumvreme)">Odbij</button>
            </td>
          </tr>

        </ng-container>
      </table>
      <h3>Narednih 5 casova</h3>
      <table>
        <tr>
          <td>
            Predmet
          </td>
          <td>
            Ucenik
          </td>
          <td>
            Udji
          </td>
        </tr>
        <ng-container *ngFor="let zahtev of casthree.slice(0, 5); let i = index">
          <tr>
            <td>
              {{ zahtev.predmet }}
            </td>
            <td>
              {{ zahtev.ucenik }}
            </td>
            <td>
              <button class="btn btn-success" type="button" (click)="odrziCas(zahtev.datumvreme)" >Odrzi Cas</button>
            </td>
          </tr>

        </ng-container>
      </table>
    </div>
    <div *ngIf="where === 3">
      <h3>MojiUcenici</h3>
      <table>
        <ng-container *ngFor="let ucenik of helper">
          <tr>
            <td>
              {{ucenik.ucenik}}
            </td>
            <td>
              <button type="button" (click) = "getAll(ucenik)">DosijeToggle</button>
            </td>
          </tr>
          <tr *ngIf="ucenik.toggle">
            <h1>Dosije</h1>
            <div *ngFor="let cas of ucenik.casovi">
              <p>{{ cas.predmet }} {{ cas.datumvreme | date: 'MM-dd-yyyy HH:mm' }}</p>
            </div>
          </tr>
        </ng-container>
      </table>
    </div>
  </main>
</body>
