import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginservisService } from '../servisi/loginservis.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private router: Router, private servis : LoginservisService){}


  username: string = ""
  password: string = ""


  login(){
    this.servis.login(this.username, this.password).subscribe(
      data=>{
        if(data==null) alert("Nema korisnika")
        else {
          localStorage.setItem("logged", data.username)
          this.router.navigate(['ucenik'])
        }
      }
    )
  }




}
Username: <input type="text" [(ngModel)]="username">
<br/>
Password: <input type="password" [(ngModel)]="password">
<br/>
<button (click)="login()">Login</button>
