import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginservisService } from '../servisi/loginservis.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private router: Router, private servis : LoginservisService){}


  username: string = ""
  password: string = ""


  login(){
    this.servis.login(this.username, this.password).subscribe(
      data=>{
        if(data==null) alert("Nema korisnika")
        else {
          localStorage.setItem("logged", data.username)
          this.router.navigate(['ucenik'])
        }
      }
    )
  }




}
<div style="display: flex; justify-content: center; align-items: center; height: 100vh;">
  <div style="text-align: center;">
    <h1>Welcome</h1>
    <form style="margin-top: 20px;">
      <div style="margin-bottom: 10px;">
        <label for="username">Username:</label>
        <input type="text" id="username" [(ngModel)]="username">
      </div>
      <div style="margin-bottom: 10px;">
        <label for="password">Password:</label>
        <input type="password" id="password" [(ngModel)]="password">
      </div>
      <button style="padding: 10px 20px; background-color: #007bff; color: #fff; border: none; border-radius: 5px; cursor: pointer;" (click)="login()">Login</button>
    </form>
  </div>
</div>
