import { Component } from '@angular/core';
import { LoginservisService } from '../servisi/loginservis.service';
import { Nastavnik } from '../models/nastavnik';
import { Cas } from '../models/cas';
import { Helper } from '../models/helper';
import { Router } from '@angular/router';
import { Predmet } from '../models/predmet';
import { ChangeDetectorRef } from '@angular/core';


@Component({
  selector: 'app-nastavnik',
  templateUrl: './nastavnik.component.html',
  styleUrls: ['./nastavnik.component.css']
})
export class NastavnikComponent {
  constructor(private servis : LoginservisService, private router : Router, private cdr: ChangeDetectorRef ) {}

  where : number = 1
  displayCasovi : number = 1
  username : string = "nastavnik3"
  nastavnik : Nastavnik = new Nastavnik()
  edited : Nastavnik = new Nastavnik()
  selectedAges: string[] = [];
  casthree : Cas[] = []
  firstFiveElements: Cas[] = []
  casovizapotvrdu : Cas[] = []
  casovizaucenike : Cas[] = []
  ucenici : string[] = []
  ucenicinodupes : string[] = []
  helper : Helper[] = []
  predmeti : Predmet[] = []
  selectedPredmeti: string[] = []
  backend: string = "http://localhost:4000"
  imgRel: string = "http://localhost:4000"
  selectedFile: File | null = null;
  selectedPDF: File | null = null;
  imageUrl : string = ""
  pdfUrl : string = ""

  ngOnInit(){
    this.where = 1
    this.username = "nastavnik3"
    let logged = localStorage.getItem("nastavnik")
    if(!logged){
      console.log("AM HERE")
      // If 'logged' is not set, navigate to the 'login' URL
      this.router.navigate(['login']);
    }
    this.servis.getpredmeti(1).subscribe(data=>{
      if(data)this.predmeti = data

    })
    this.servis.getnastavnik(this.username).subscribe(nst=>{
      this.nastavnik = nst
      this.imgRel += nst.profilePic
      this.pdfUrl = this.backend + nst.CV
    })
    this.selectedAges = []
    this.edited = new Nastavnik()
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
      for(let a of this.casovizapotvrdu){
        this.servis.casget(1,a.ucenik).subscribe(data=>{
          if(data){
            let sum = 0
            let num = 0
            for(let cas of data){
              if(cas.ocena !== 0){
                num++;
                sum += cas.ocena
              }
            }
            if(num >= 3){
              a.avgOcena = sum/num
            }
          }
        })
      }
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi


    })
    this.servis.getsvojeucenike(this.username).subscribe(casovi=>{
      this.casovizaucenike = casovi
      for (let cas of this.casovizaucenike){
        this.ucenici.push(cas.ucenik)
      }
      this.ucenicinodupes = Array.from(new Set(this.ucenici))
      for (let ucenik of this.ucenicinodupes){
        let hlp = new Helper()
        hlp.ucenik = ucenik


        this.helper.push(hlp)
      }

    })





  }

  updateCas(cas : Cas){
    if(cas.ocena>5 || cas.ocena < 0){
      alert("Ocena moze biti od 1 do 5 ")
      return
    }
    this.servis.updateCas(cas).subscribe(data=>{
      console.log(data.message)
    })

    this.servis.getsvojeucenike(this.username).subscribe(casovi=>{
      this.casovizaucenike = casovi
      for (let cas of this.casovizaucenike){
        this.ucenici.push(cas.ucenik)
      }
      this.ucenicinodupes = Array.from(new Set(this.ucenici))
      for (let ucenik of this.ucenicinodupes){
        let hlp = new Helper()
        hlp.ucenik = ucenik


        this.helper.push(hlp)
      }

    })
  }
  getAll(name : Helper){
    if(name.toggle){
      name.toggle = 0
    }else{

      name.toggle = 1
    }

  }


  changeWhere(b : number){
    this.where = b;
    if(b == 3){
      for(let hlp of this.helper){
        this.servis.getdosije(hlp.ucenik,this.username).subscribe(casovi=>{
          hlp.casovi = casovi
        })
      }
    }
  }


  changeDisplay(b : number){
    this.displayCasovi = b;
  }


  saveChanges(){
    if(this.selectedPredmeti.length === 0){
      this.edited.predmeti = this.nastavnik.predmeti
    }else{
      this.edited.predmeti = this.selectedPredmeti
    }
    this.edited.username = this.nastavnik.username
    if(this.edited.name  === "")this.edited.name = this.nastavnik.name
    if(this.edited.surname  === "")this.edited.surname = this.nastavnik.surname
    if(this.edited.adress  === "")this.edited.adress = this.nastavnik.adress
    if(this.edited.email  === "")this.edited.email = this.nastavnik.email
    if(this.edited.telephone  === "")this.edited.telephone = this.nastavnik.telephone
    if(this.selectedAges.length === 0){
      this.edited.razredi = this.nastavnik.razredi
    }else{
      this.edited.razredi = this.selectedAges
    }
    if(this.selectedFile !== null){
      this.uploadTest().then(()=>{
        this.edited.profilePic = this.imageUrl
        if(this.selectedPDF !== null){
          this.uploadPDF().then(()=>{
            console.log("USAO")
            this.edited.CV = this.pdfUrl
            this.servis.updatenastavnik(this.edited).subscribe(data=>{
              this.servis.getnastavnik(this.username).subscribe(nst=>{
                this.nastavnik = nst
                console.log(this.nastavnik.profilePic)

              })
            })

          })
        }else{
          this.servis.updatenastavnik(this.edited).subscribe(data=>{

          })
          this.servis.getnastavnik(this.username).subscribe(nst=>{
            this.nastavnik = nst
          })
        }
      })
    }else if(this.selectedPDF !== null){
      this.uploadPDF().then(()=>{
        this.edited.CV = this.pdfUrl
        this.servis.updatenastavnik(this.edited).subscribe(data=>{

        })
        this.servis.getnastavnik(this.username).subscribe(nst=>{
          this.nastavnik = nst
        })
      })
    }else{
      this.servis.updatenastavnik(this.edited).subscribe(data=>{

      })
      this.servis.getnastavnik(this.username).subscribe(nst=>{
        this.nastavnik = nst
      })
    }
    //this.ngOnInit()

  }

  toggleAge(age: string) {
    if (this.selectedAges.includes(age)) {

      this.selectedAges = this.selectedAges.filter(item => item !== age);
    } else {
      // Dodaj uzrast ako ne postoji u nizu
      this.selectedAges.push(age);
    }


  }


  prihvatiCas(datum : Date){
    this.servis.potvrdiCas(datum,this.username,1).subscribe(data =>{

    })
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi


    })




  }

  odbijCas(datum : Date){
    this.servis.potvrdiCas(datum,this.username,2).subscribe(data =>{
      alert(data.message)
    })
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi

    })

  }

  odrziCas(datum : Date){
    this.servis.odrziCas(datum,this.username,1).subscribe(data =>{

    })
    this.servis.getpotvrda(this.username).subscribe(casovi =>{
      this.casovizapotvrdu = casovi
    })
    this.servis.getthree(this.username).subscribe(casovi =>{
      this.casthree = casovi

    })
    this.servis.getsvojeucenike(this.username).subscribe(casovi=>{
      this.casovizaucenike = casovi
      for (let cas of this.casovizaucenike){
        this.ucenici.push(cas.ucenik)
      }
      this.ucenicinodupes = Array.from(new Set(this.ucenici))
      for (let ucenik of this.ucenicinodupes){
        let hlp = new Helper()
        hlp.ucenik = ucenik


        this.helper.push(hlp)
      }

    })

  }

  LogOut(){
    localStorage.clear()
    this.router.navigate([''])
  }

  toggleSelection(predmet: string) {
    if (this.selectedPredmeti.includes(predmet)) {
      this.selectedPredmeti = this.selectedPredmeti.filter(p => p !== predmet);
    } else {
      this.selectedPredmeti.push(predmet);
    }
  }

  openPdf() {
    if (this.pdfUrl) {
      window.open(this.pdfUrl, '_blank');
    }
  }

  onImageSelected(event: any): void {
    this.selectedFile = event.target.files[0];


  }

  onPdfSelected(event: any): void {
    this.selectedPDF = event.target.files[0];


  }


  uploadTest(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      this.servis.uploadPicture(this.selectedFile!).subscribe(data => {
        if (data && data.filename) {
          console.log('File uploaded successfully.');
          this.imageUrl = data.filename;

          resolve();
        } else {
          reject(new Error('Upload failed'));
        }
      });
    });
  }


  uploadPDF(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      this.servis.uploadPicture(this.selectedPDF!).subscribe(data => {
        if (data && data.filename) {
          console.log('File uploaded successfully.');
          this.pdfUrl = data.filename;

          resolve();
        } else {
          reject(new Error('Upload failed'));
        }
      });
    });
  }






}
<header class="navbar navbar-dark-grey">
  <div class="container">
    <div class="navbar-right">
      <span (click)="changeWhere(1)">Profil  </span>
      <span (click)="changeWhere(2)">Casovi  </span>
      <span (click)="changeWhere(3)">Moji ucenici  </span>
      <span (click)="LogOut()">Log out</span>
    </div>
  </div>

</header>



<body>


  <main>
    <div *ngIf="where === 1">
      <div class="container">
        <div class="row justify-content-center">
            <div class="col-md-6">
                <div class="card" style = "width:400px">
                    <img class="card-img-top" [src]="imgRel" >
                    <div class="card-body">
                        <h4 class="card-title">{{ nastavnik.name }} {{ nastavnik.surname }}</h4>
                        <p class="card-text">Adresa:{{ nastavnik.adress }}</p>
                        <p class="card-text">Email:{{ nastavnik.email }}</p>
                        <p class="card-text">Telefon:{{ nastavnik.telephone }}</p>
                        <p class="card-text">Predmeti:
                            <ng-container *ngFor="let predmet of nastavnik.predmeti">
                                <div class="indented">{{ predmet }}</div>
                            </ng-container>
                        </p>
                        <p class="card-text">Razredi:
                            <ng-container *ngFor="let razred of nastavnik.razredi">
                                <div class="indented">{{ razred }}</div>
                            </ng-container>
                        </p>
                        <p class="card-text">
                            <a (click)="openPdf()" style="cursor: pointer;">
                                <mat-icon>description</mat-icon>
                                Moj CV
                            </a>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    </div>
      <hr>
      <h3>Uredi Profil</h3>
      <label for="name">Ime:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="name" [(ngModel)]="edited.name" >
      <br/>

      <label for="surname">Prezime:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="surname" [(ngModel)]="edited.surname" >
      <br/>

      <label for="adress">Adresa:</label>
      <input class = "btn-outline-dark rounded-pill" type="text" id="adress" [(ngModel)]="edited.adress" >
      <br/>

      <label for="email">Email:</label>
      <input class = "btn-outline-dark rounded-pill" type="email" id="email" [(ngModel)]="edited.email" >
      <br/>

      <label for="telephone">Telefon:</label>
      <input class = "btn-outline-dark rounded-pill" type="tel" id="telephone" [(ngModel)]="edited.telephone" >
      <br/>
      <p>Razredi(izaberite sve razrede kojima biste predavali):</p>
      <label>
        <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('osnovna1-4')" (change)="toggleAge('osnovna1-4')">
        Osnovna škola 1-4. razred
      </label>
      <br/>
      <label>
        <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('osnovna5-8')" (change)="toggleAge('osnovna5-8')">
        Osnovna škola 5-8. razred
      </label>
      <br/>
      <label>
        <input class = "btn-outline-dark rounded-pill" type="checkbox" [checked]="selectedAges.includes('srednja')" (change)="toggleAge('srednja')">
        Srednja škola
      </label>
      <br/>
      Koje predmete bi predavali?
      <br/>
      <div class="checkbox-container">
        <label *ngFor="let predmet of predmeti">
          <input class="btn-outline-dark rounded-pill" type="checkbox" name="predmeti" [value]="predmet.predmet" (change)="toggleSelection(predmet.predmet)">
          {{ predmet.predmet }}
        </label>
      </div>
      <br/>
      Profilna: <input class="btn-outline-dark " type="file" (change)="onImageSelected($event)" accept="image/*"  />
      <br/>
      CV: <input class="btn-outline-dark " type="file" (change)="onPdfSelected($event)" accept=".pdf" />
      <br/>

      <button class="btn btn-primary" type="button" (click)="saveChanges()">Sačuvaj</button>


    </div>
    <div *ngIf="where === 2">
      <div class="row">
        <div class="col-md-12">
          <h3>Tabela zahteva</h3>
          <table class="table">
            <thead>
              <tr>
                <th>Učenik</th>
                <th>Predmet</th>
                <th>Opis</th>
                <th>Prihvati</th>
                <th>Odbij</th>
              </tr>
            </thead>
            <tbody>
              <ng-container *ngFor="let zahtev of casovizapotvrdu">
                <tr>
                  <td>{{ zahtev.ucenik }}</td>
                  <td>{{ zahtev.predmet }}</td>
                  <td>{{ zahtev.opis }}</td>
                  <td>
                    <button class="btn btn-success" type="button" (click)="prihvatiCas(zahtev.datumvreme)">Prihvati</button>
                  </td>
                  <td>
                    <button class="btn btn-danger" type="button" (click)="odbijCas(zahtev.datumvreme)">Odbij</button>
                  </td>
                </tr>
              </ng-container>
            </tbody>
          </table>
        </div>
      </div>
      <div class="row">
        <div class="col-md-12">
          <h3>Narednih 5 časova</h3>
          <button class="btn btn-info  mr-2" type="button" (click)="changeDisplay(1)">5 casova u 3 dana</button>
          <button class="btn btn-info  mr-2" type="button" (click)="changeDisplay(2)">10 casova u 3 dana</button>
          <button class="btn btn-info " type="button" (click)="changeDisplay(3)">Svi casovi u 3 dana</button>
          <table class="table">
            <thead>
              <tr>
                <th>Datum</th>
                <th>Predmet</th>
                <th>Učenik</th>
                <th>Udji</th>
              </tr>
            </thead>
            <tbody *ngIf="displayCasovi === 1">
              <ng-container *ngFor="let zahtev of casthree.slice(0, 5); let i = index">
                <tr>
                  <td>{{ zahtev.datumvreme | date: 'MM-dd-yyyy HH:mm' }}</td>
                  <td>{{ zahtev.predmet }}</td>
                  <td>{{ zahtev.ucenik }}</td>
                  <td>
                    <button class="btn btn-success" type="button" (click)="odrziCas(zahtev.datumvreme)">Odrzi Cas</button>
                  </td>
                </tr>
              </ng-container>
            </tbody>
            <tbody *ngIf="displayCasovi === 2">
              <ng-container *ngFor="let zahtev of casthree.slice(0, 10); let i = index">
                <tr>
                  <td>{{ zahtev.datumvreme | date: 'MM-dd-yyyy HH:mm' }}</td>
                  <td>{{ zahtev.predmet }}</td>
                  <td>{{ zahtev.ucenik }}</td>
                  <td>
                    <button class="btn btn-success" type="button" (click)="odrziCas(zahtev.datumvreme)">Odrzi Cas</button>
                  </td>
                </tr>
              </ng-container>
            </tbody>
            <tbody *ngIf="displayCasovi === 3">
              <ng-container *ngFor="let zahtev of casthree">
                <tr>
                  <td>{{ zahtev.datumvreme | date: 'MM-dd-yyyy HH:mm' }}</td>
                  <td>{{ zahtev.predmet }}</td>
                  <td>{{ zahtev.ucenik }}</td>
                  <td>
                    <button class="btn btn-success" type="button" (click)="odrziCas(zahtev.datumvreme)">Odrzi Cas</button>
                  </td>
                </tr>
              </ng-container>
            </tbody>
          </table>
        </div>
      </div>
    </div>
    <div *ngIf="where === 3">
      <div class="row">
        <div class="col-md-12">
          <h3>Moji Učenici</h3>
          <table class="table">
            <tbody>
              <ng-container *ngFor="let ucenik of helper">
                <tr>
                  <td>{{ ucenik.ucenik }}</td>
                  <td>
                    <button class="btn btn-info" type="button" (click)="getAll(ucenik)">Dosije Toggle</button>
                  </td>
                </tr>
                <tr *ngIf="ucenik.toggle">
                  <td colspan="2">
                    <h4>Dosije</h4>
                    <div class="list-group">
                      <span *ngFor="let cas of ucenik.casovi; let i=index" class="list-group-item">
                        {{ cas.predmet }} - {{ cas.datumvreme | date: 'MM-dd-yyyy HH:mm' }}
                        <input class = "btn-outline-dark rounded-pill"  type="number"  [(ngModel)]="ucenik.casovi[i].ocena" placeholder = "Ocena...">
                        <input class = "btn-outline-dark rounded-pill"  type="text" [(ngModel)]="ucenik.casovi[i].komentar" placeholder = "OpcioniKomentar...">&nbsp;
                        <button class="btn btn-primary" type="button" (click)="updateCas(cas)">Ostavi ocenu/komentar</button>
                      </span>
                    </div>
                  </td>
                </tr>
              </ng-container>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </main>
  <footer>
    <div class="footer-content">
      <p>PIA Projekat</p>
    </div>
  </footer>
</body>
